(36, 'woocommerce-WCSubscriptions-June-2023-updated-needed', 'marketing', 'en_US', 'Action required: Security update of WooCommerce Subscriptions', '<strong>Your store requires a security update for the WooCommerce Subscriptions plugin</strong>. Please update the WooCommerce Subscriptions plugin immediately to address a potential vulnerability.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(37, 'woocommerce-WCReturnsWarranty-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Returns and Warranty Requests extension', '<strong>Your store requires a security update for the Returns and Warranty Requests extension</strong>.  Please update to the latest version of the WooCommerce Returns and Warranty Requests extension immediately to address a potential vulnerability discovered on May 31.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(38, 'woocommerce-WCOPC-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce One Page Checkout', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(39, 'woocommerce-WCGC-July-2023-update-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce GoCardless Extension', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce GoCardless extension</strong>. The fix for this vulnerability was released on July 4th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(40, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(41, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(42, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(43, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(44, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(45, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'uk-vat-notice-2025-04-30', 'warning', 'en_US', 'UK VAT numbers are no longer supported', 'Due to recent HM Revenue &amp; Customs (HMRC) VAT API changes, your EU VAT Number for WooCommerce extension can no longer validate UK VAT numbers. HMRC now requires merchants to register an API key, which this extension does not currently support. We recommend EU/UK VAT Validation Manager for WooCommerce as a replacement', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'payson-retirement-2025', 'update', 'en_US', 'Payson Form Retirement', 'Svea Bank is discontinuing the Payson service; the last date for transactions is August 31, 2025. As a result, the Payson Form extension has been removed from WooCommerce.com. Documentation remains available.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'paypal-braintree-sdk-depreciation-2025-05-30', 'update', 'en_US', 'Action required: Update the PayPal Braintree extension', 'Your PayPal Braintree extension is using an outdated SDK and will stop processing payments after June 30, 2025, due to a root SSL certificate expiration. To avoid service disruption, update to version 3.2.4 or newer immediately.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'stripe_optimizedcheckout_upgrade_q3_2025', 'marketing', 'en_US', 'Upgrade for a faster, more flexible Stripe checkout', 'You\'re not on the latest WooCommerce Stripe Payment Gateway version. Upgrade to version 9.8 to access Stripe\'s Optimized Checkout Suite and leverage AI models to dynamically surface the most relevant payment methods for each customer, including BNPL, local options, and one-click Link checkout.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'square_ordersync_upgrade_q3_2025', 'marketing', 'en_US', 'You asked, we delivered — order sync is here for Square', 'Update your Square extension to v5.0 to access <a href=\"https://woocommerce.com/document/woocommerce-square/sync-settings/sync-orders-between-woocommerce-and-square/\">automatic order syncing</a>. Manage your online sales in Square, keep reports accurate, and save time with order updates visible across both platforms.', '{}', 'pending', 'woocommerce.com', '2025-09-13 13:04:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'woopayments-new-lpms-2025-06-30', 'marketing', 'en_US', 'Get the POS built specifically for Woo stores', 'Introducing WooCommerce POS, our new point-of-sale solution built to get you up and running quickly with in-person sales. Search products, scan barcodes, apply coupons, and send email receipts — right in the WooCommerce mobile app, with all of your online and physical sales synced in real time — no extra setup required', '{}', 'pending', 'woocommerce.com', '2025-09-15 06:27:14', NULL, 0, 'plain', '', 0, 0, 'info');
